(4, 'WordPress Theme Development', 'lms-chhutTme.webp', 'WordPress', 'Build professional, customizable WordPress themes from scratch with hands-on development techniques and best practices.', 1, NULL, '2024-11-26 13:50:43', '2024-12-05 21:48:33'),
(5, 'Product Design', 'lms-RBpAX5xb.webp', 'Design', 'Master product design essentials, from research to prototyping, to create user-centered, impactful designs.', 1, NULL, '2024-11-26 13:53:48', '2024-12-05 21:48:47'),
(6, 'App Development With Flutter', 'lms-yEyUuOnJ.webp', 'Flutter', '<div><div><div><div><p>Learn Flutter to build cross-platform, high-performance mobile applications with a single codebase.</p></div></div></div></div>', 1, NULL, '2024-11-26 13:58:40', '2024-12-05 21:48:58'),
(7, 'Webflow Development', 'lms-f8d1IcYs.webp', 'Webflow', 'Master Webflow to design and develop responsive, visually stunning websites without writing code.', 1, NULL, '2024-11-26 14:01:25', '2024-12-05 21:49:05'),
