(9, 'Artificial Intelligence', 'lms-j7HRkUXb.webp', 'Learn Artificial Intelligence - From Basics to Advanced AI Concepts', 'Become an AI expert by understanding the fundamentals of artificial intelligence. Learn topics like neural networks, deep learning, natural language processing, and reinforcement learning to create smart and adaptive systems.', 1, NULL, '2024-11-27 11:37:07', '2024-12-05 21:49:36'),
(10, 'Graphic Design', 'lms-NpyT3Sfq.webp', 'Graphic Design Courses - Create Stunning Visuals', 'Explore the world of graphic design with courses covering typography, color theory, branding, and software like Adobe Photoshop, Illustrator, and InDesign. Perfect for anyone looking to create impactful designs.', 1, NULL, '2024-11-27 11:37:44', '2024-12-05 21:49:50'),
(11, 'Blockchain Technology', 'lms-o8bbgR8J.webp', 'Learn Blockchain Technology - Bitcoin, Ethereum, and Beyond', 'Understand the underlying technology of cryptocurrencies. Learn about decentralized ledgers, smart contracts, and blockchain development tools. These courses will prepare you for careers in fintech and beyond.', 1, NULL, '2024-11-27 11:38:37', '2024-12-05 21:50:08'),
(12, 'Motion Graphics', 'lms-g1keiTyA.webp', 'Motion Graphics - Bring Your Designs to Life', 'Learn to create animated content using After Effects, Cinema 4D, and other tools. Discover the art of storytelling through motion and elevate your creative projects.', 1, NULL, '2024-11-27 11:39:16', '2024-12-05 21:50:41'),
(13, 'Ethical Hacking', 'lms-XnNPczu0.webp', 'Ethical Hacking - Learn Penetration Testing & Network Security', 'Protect systems and networks by learning ethical hacking techniques. Dive into penetration testing, vulnerability analysis, and real-world cybersecurity strategies.', 1, NULL, '2024-11-27 11:39:47', '2024-12-05 21:50:53'),
(14, 'E-Commerce Development', 'lms-xORIeuXy.webp', 'E-Commerce Development - Build Online Stores with Ease', 'Learn how to create and manage online stores using platforms like Shopify, WooCommerce, and Magento. Explore features like payment integration, product listing, and user experience optimization.', 1, NULL, '2024-11-27 11:40:54', '2024-12-05 21:51:09');
